import React from "react";
import { useForm } from "react-hook-form";
import { Context } from "./context";


const Modal = ({modalActive, setModalActive, newValue, setNewValue, isEditing, id}) => {


  const {
    register,
    formState: {
      errors,
    },
    handleSubmit,
  } = useForm();

  const onSubmit = (data) => {
    setNewValue(data.todoValue);
    setModalActive(false);
}

