import React, { useState, useEffect } from "react";
import ToDoElements from "./toDoElements";
import Modal from "./Modal";
import { Context } from "./context";

const Main = () => {
  const [todos, setTodos] = useState(
    JSON.parse(localStorage.getItem("todos")) || []
  );
  const [todoValue, setTodoValue] = useState("");
  const [modalActive, setModalActive] = useState(false);
  const [newValue, setNewValue] = useState("hui");
  const [isEditing, setIsEditing] = useState(false);
  const [selectedTodoId, setSelectedTodoId] = useState()

  const addTodo = (event) => {
    if (event.key === "Enter") {
      setTodos([
        ...todos,
        {
          id: Date.now(),
          value: todoValue,
          completed: false,
        },
      ]);
      setTodoValue("");
    }
  };

  const addTodobtn = (todoValue) => {
      setTodos([
        ...todos,
        {
          id: Date.now(),
          value: todoValue,
          completed: false,
        },
      ]);
      setTodoValue("");
  };

  const openModal = (id) => {
    setSelectedTodoId(id);
  }

  const editTodo = (id) => {
    setTodos([
      ...todos.map((todos) =>
        todos.id === id
          ? { ...todos, value: newValue }
          : { ...todos }
      ),
    ]);
  };


  const removeTodo = (id) => {
    if (id) {
      setTodos([...todos.filter((todos) => todos.id !== id)]);
    }
  };

  const toggleTodo = (id) => {
    setTodos([
      ...todos.map((todos) =>
        todos.id === id
          ? { ...todos, completed: !todos.completed }
          : { ...todos }
      ),
    ]);
  };

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  return (
    <Context.Provider
      value={{
        removeTodo,
        toggleTodo,
        editTodo,
      }}
    >
      <>
        <div className="container">
          <div className="toDoList">
            <h1 className="pageTitle">To Do List</h1>
            <div className="input">
            <textarea
              type="text"
              className="toDoInput"
              placeholder="What do you need to do?"
              value={todoValue}
              onChange={(event) => setTodoValue(event.target.value)}
              onKeyPress={addTodo}
              autoFocus
            />
            <button className="addTodobtn" onClick={() => addTodobtn(todoValue)}>+</button>
            </div>
            <ToDoElements
              todos={todos}
              toggleTodo={toggleTodo}
              removeTodo={removeTodo}
              editTodo={editTodo}
              setIsEditing={setIsEditing}
              setModalActive={setModalActive}
              setNewValue={setNewValue}
              newValue={newValue}
              selectedTodoId={selectedTodoId}
              setSelectedTodoId={setSelectedTodoId}
              openModal={openModal}
            />
          </div>
        </div>
      </>
      <Modal
      modalActive={modalActive}
      setModalActive={setModalActive}
      newValue={newValue}
      setNewValue={setNewValue}
      isEditing={isEditing}
      />
    </Context.Provider>
  );
};

export default Main;
