import React, { useContext, useState } from "react";
import { Context } from "./context";

export default function ToDoItem({ id, value, completed, setIsEditing, setModalActive, setNewValue, newValue}) {
  const { toggleTodo, removeTodo, editTodo } = useContext(Context);

  const [taskValue, setTaskValue] = useState(value);

  return (
    <li>
      <label className="toDoItem">
        <input
          type="checkbox"
          className="customCheckbox"
          defaultChecked={completed}
          onClick={() => {
            toggleTodo(id);
          }}
        />
        <span
          className={completed ? "strikeText" : "value"}
          onClick={() => {
            toggleTodo(id);
          }}
        >
          {taskValue}
        </span>
        <button
          className="editTodo"
          onClick={() => {
            setIsEditing(true);
            setTaskValue(taskValues[id] || value);
            setModalActive(true);
          }}
        >
          <img
            src="https://cdn-icons-png.flaticon.com/512/7398/7398464.png"
            alt="Edit"
          />
        </button>
        <button
          className="deleteTodo"
          onClick={() => {
            removeTodo(id);
          }}
        >
          <span></span>
        </button>
      </label>
    </li>
  );
}
