import React, {useState} from "react";
import { useForm } from "react-hook-form";


const Modal = ({active, setActive}) => {
  const [value, setValue] = useState("");

  const {
    register,
    formState: {
      errors,
    },
    handleSubmit,
  } = useForm();

  

  return(
    <div className={active ? "modal active" : "modal"} onClick={() => setActive(false)}>
      <form onSubmit={handleSubmit()} className="modalContent" onClick={(e) => e.stopPropagation()}>
          <textarea className="formInput" type="text" value={value} onChange={(e) => setValue(e.target.value)}/>
          
          <input type="submit" />
      </form>
    </div>
  )
}

export default Modal;
