import React, { useContext } from "react";
import { useForm } from "react-hook-form";
import { Context } from "./context";


const Modal = ({modalActive, setModalActive, todos, selectedTodoId, selectedTodoValue, setSelectedTodoValue}) => {
  const { editTodo, editDescription } = useContext(Context);

  
  const {
    register,
    formState: {
      errors,
    },
    handleSubmit,
  } = useForm();


  const onSubmit = (data) => {
    editTodo(data.todoValue);
    setModalActive(false);
  }

  const handleInputChange = (e) => {
    const newValue = e.target.value;
    setSelectedTodoValue(newValue);
  };

  return(
    <div className={modalActive ? "modal active" : "modal"} onClick={() => setModalActive(false)}>
      <form onSubmit={handleSubmit(onSubmit)} className="modalContent" onClick={(e) => e.stopPropagation()}>
          <label>
            Title:
            <textarea
            {...register('todoValue')}
            className="formInput"
            value={selectedTodoValue}
            onChange={handleInputChange}
            />
          </label>
          <button type="submit" className="submitButton">Submit</button>
      </form>
    </div>
  )
}

export default Modal;
