import React, {useState} from "react";
import { useForm } from "react-hook-form";


const Modal = ({active, setActive, newValue, setNewValue, editTodo, id, todos}) => {
  const [isEditing, setIsEditing] = useState(false);

  const {
    register,
    formState: {
      errors,
    },
    handleSubmit,
  } = useForm();

  const onSubmit = (data) => {
    setNewValue(data.todoValue);
    editTodo(id);
    setActive(false);
}

  return(
    <div className={active ? "modal active" : "modal"} onClick={() => setActive(false)}>
      <form onSubmit={handleSubmit(onSubmit)} className="modalContent" onClick={(e) => e.stopPropagation()}>
          <label>
            Value:
            <textarea
            {...register('todoValue')}
            className="formInput"
            defaultValue={isEditing ? newValue : ''}
            />
          </label>
          
          <input type="submit"/>
      </form>
    </div>
  )
}

export default Modal;
