import React, {useState} from "react";
import { useForm } from "react-hook-form";


const Modal = ({active, setActive, editTodo}) => {
  const [value, setValue] = useState("");

  const {
    register,
    formState: {
      errors,
    },
    handleSubmit,
  } = useForm();

  const onSubmit = (data) => {
  const newValue = data.todoValue
    alert(JSON.stringify(newValue))
  }

  return(
    <div className={active ? "modal active" : "modal"} onClick={() => setActive(false)}>
      <form onSubmit={handleSubmit(onSubmit)} className="modalContent" onClick={(e) => e.stopPropagation()}>
          <label>
            Value:
            <textarea
            {...register('todoValue')}
            className="formInput"
            //value={value} 
            //onChange={(e) => setValue(e.target.value)}
            />
          </label>
          
          <input type="submit" onClick={() => editTodo(data.todoValue)}/>
      </form>
    </div>
  )
}

export default Modal;
