import React, { useContext, useState } from "react";
import { Context } from "./context";

export default function ToDoItem({ id, value, completed, setIsEditing }) {
  const { toggleTodo, removeTodo, editTodo } = useContext(Context);
  const [newValue, setNewValue] = useState(value); // Локальний стан для збереження нового значення

  return (
    <li>
      <label className="toDoItem">
        <input
          type="checkbox"
          className="customCheckbox"
          defaultChecked={completed}
          onClick={() => {
            toggleTodo(id);
          }}
        />
        <span
          className={completed ? "strikeText" : "value"}
          onClick={() => {
            toggleTodo(id);
          }}
        >
          {newValue}
        </span>
        <button
          className="editTodo"
          onClick={() => {
            setIsEditing(true);
            setNewValue(value); // Встановити нове значення при редагуванні
          }}
        >
          Редагувати
        </button>
        <button
          className="deleteTodo"
          onClick={() => {
            removeTodo(id);
          }}
        >
          Видалити
        </button>
      </label>
    </li>
  );
}
